import axios from "axios";
import React from 'react';

export default class TitleInput extends React.Component {
    /* state = {
        title: "",
        content: "",
    }; */

    handleTitle = event => {
        this.setState( {
            title: event.target.value
        });
    };

    handleContent = event => {
        this.setState( {
            content: event.target.value
        });
    };

    handleSubmit = event => {
        event.preventDefault();

        /* const article = {
            title: this.state.title,
            content: this.state.content
        };

        axios.post(`http://localhost:5000/articles`, { article, headers: {
            //'Content-Type': 'application/x-www-form-urlencoded; charset=UTF-8',
            'Content-Type': 'application/json'
        } })
            .then(res => {
                console.log(res);
                console.log(res.data);
            }) */

        axios.post('http://localhost:5000/articles', {
            title: this.state.title,
            content: this.state.content
            })
            .then(function (response) {
            console.log(response);
            })
            .catch(function (error) {
            console.log(error);
            });
    }

    render() {
        return(
            <form onSubmit={this.handleSubmit}>
                <label>
                    Title: 
                    <input type='text' name='title' onChange={this.handleTitle}></input>
                </label>
                <label>
                    Content: 
                    <input type='text' name='content' onChange={this.handleContent}></input>
                </label>
                <button type='submit'>Add</button>
            </form>
        );
    }
}
