import axios from "axios";
import React from 'react';
import styled from 'styled-components';

export default class TitleDelete extends React.Component {
    state = {
        title: "", 
    };

    handleDelete = event => {
        this.setState( {
            title: event.target.value
        });
    };

    handleSubmit = event => {
        event.preventDefault();

        axios.delete('http://localhost:5000/articles/:title', {
            title: this.state.title,
            })
            .then(function (response) {
            console.log(response);
            })
            .catch(function (error) {
            console.log(error);
            });
        
    }

    Reload = event => {
        document.location.reload(true);
    }

    render() {
        return(
            <Formulario onSubmit={this.handleSubmit}>
                <h1>Coloque Aqui o TÃ­tulo a ser Removido</h1>

                <label>
                    <p>Title:</p> 
                    <input className='title' type='text' name='title' onChange={this.handleDelete}></input>
                </label>
            <br></br>
                <button type='submit' onClick={this.Reload}>Deletar</button>
            </Formulario>
        );
    }
}

const Formulario = styled.form`
    text-align: center;
    color: white;
    padding: 1% 0;


    label {
        color: white;
        font-size: 150%;
        font-weight: 500;
    }

    .title {
        margin: 0.5%; 
        width: 50%;
    }

    button {
        background-color: rgb(43, 226, 58);
        margin-top: 5%;
        border-style: none;
        height: 50px;
        width: 30%;
        color: white;
        font-size: 150%;
        font-weight: bold;
    }
`;
