import axios from "axios";
import React from 'react';

export default class TitleInput extends React.Component {
    state = {
        title: "",
        content: "",
    };

    handleSubmit = event => {
        event.preventDefault();

        const article = {
            title: this.state.title,
            content: this.state.content
        };

        axios.post(`http://localhost:5000/articles`, {article})
    }

    componentDidMount() {
        axios.get(`http://localhost:5000/articles`)
            .then(res => {
                console.log(res);
                this.setState({ names: res.data });
            })
            .catch(err => {
                console.log(err);
                this.setState(err);
            });
    }

    render() {
        return(
            <ul>
                { this.state.names.map(name => 
                <li key ={name.id}>
                    <h3>{name.title}</h3>
                    <h5>{name.content}</h5>
                </li>) 
                }
            </ul>
        );
    }
}
