import axios from "axios";
import React from 'react';

export default class TitleInput extends React.Component {
    state = {
        title: "",
        content: "",
    };

    handleTitle = event => {
        this.setState( {
            title: event.target.value
        });
    };

    handleContent = event => {
        this.setState( {
            content: event.target.value
        });
    };

    handleSubmit = event => {
        event.preventDefault();

        const article = {
            title: this.state.title,
            content: this.state.content
        };

        axios.post(`http://localhost:5000/articles`, { article })
            .then(res => {
                console.log(res);
                console.log(res.data);
            })
    }

    render() {
        return(
            <form onSubmit={this.handleSubmit}>
                <label>
                    Title: 
                    <input type='text' name='title' onChange={this.handleTitle}></input>
                </label>
                <label>
                    Content
                    <input type='text' name='content' onChange={this.handleContent}></input>
                </label>
                <button type='submit'>Add</button>
            </form>
        );
    }
}
