import axios from "axios";
import React from 'react';

export default class TitleInput extends React.Component {
    

    handleSubmit = event => {
        event.preventDefault();

        axios.post('http://localhost:5000/articles', {
            title: this.state.title,
            content: this.state.content
            })
            .then(function (response) {
            console.log(response);
            })
            .catch(function (error) {
            console.log(error);
            });
    }

    render() {
        return(
            <form onSubmit={this.handleSubmit}>
                <label>
                    Title: 
                    <input type='text' name='title' onChange={this.handleTitle}></input>
                </label>
                <label>
                    Content: 
                    <input type='text' name='content' onChange={this.handleContent}></input>
                </label>
                <button type='submit'>Add</button>
            </form>
        );
    }
}
